mutation upsertPlace($query: PlaceQueryInput!, $data: PlaceInsertInput!) {
  upsertOnePlace(query: $query, data: $data) {
    _id
    name
    address {
      postalCode
      street
    }
    owner {
     workspace {
       name
     }
    }
  }
}

mutation upsertShippingMethod($query: ShippingMethodQueryInput!, $data: ShippingMethodInsertInput!) {
  upsertOneShippingMethod(query: $query, data: $data) {
    _id
    name
    owner {
      workspace {
        name
      }
    }
  }
}

query getOrderRelationships($shippingAddressPlaceIds: [String!]!, $shippingMethodIds: [String!]!, $linesProductsIds: [String!]!, $linesCatalogItemsIds: [String!]!) {
  shippingAddressPlace: place(query: {ids_in: $shippingAddressPlaceIds}) {
    _id
    address {
      street
      postalCode
    }
  }
  shippingMethod: shippingMethod(query: {ids_in: $shippingMethodIds}) {
    _id
    name
  }
  lineProducts: products(query: {ids_in: $linesProductsIds}) {
    _id
    ids
    name
  }
  lineItems: catalogItems(query: {ids_in: $linesCatalogItemsIds}) {
    _id
    ids
    name
  }
}

query getOrderRelationships($sellerWorkspaceIds: [String!]!, $brokerMarketplaceKey: String!) {
  sellerWorkspace: workspace(query: {ids_in: $sellerWorkspaceIds}) {
    _id
    name
  }
  brokerWorkspace: workspace(query: {marketplace: {key: $brokerMarketplaceKey}}) {
    _id
    name
  }
}

parameters.functionCode:
const order = $node["Enrich"].json
const orderRelationships = $node["Get order first relationships"].json.data

order.seller.workspace =  {
  link: orderRelationships.sellerWorkspace._id
}

order.broker.workspace =  {
  link: orderRelationships.brokerWorkspace._id
}

if(order.shippingAddress.place.geo == null){
  const mapsShippingGeo = $node["Geocode"].json.results[0].geometry.location
  order.shippingAddress.place.geo = {
    type: "Point",
    coordinates: [mapsShippingGeo.lng, mapsShippingGeo.lat]
  }
}

const owner = {
  workspace: {
    link: order.seller.workspace.link
  }
}

order.shippingAddress.place.owner = owner

if(order.shippingMethod != null)
  order.shippingMethod.owner = owner

order.lines.forEach(l => l.product.owner = owner)
order.lines.forEach(l => l.item.owner = owner)

order.products = order.lines.map(l => l.product);
order.productsIds = order.lines.reduce((acc,val) => acc = acc.concat(val.product.ids),[]);

order.catalogItems = order.lines.map(l => l.item)
order.catalogItemsIds = order.lines.reduce((acc,val) => acc = acc.concat(val.item.ids),[]);
return order

